import { useState } from "react"
import { save } from "./save"

export const EditorOpctions = () => {
  const [show, setShow] = useState(false)

  const width = 100

  const inputStyle = {
    border: 0,
    //padding: `8px 16px`,
    height: 30
  }

  return <div
    style={{
      position: `absolute`,
      zIndex: 1,
      display: `flex`,
      flexDirection: `column`,
      left: window.editor.width - 100
    }}
    onMouseEnter={() => {
      setShow(true)
    }}
    onMouseLeave={() => {
      setShow(false)
    }}
  >
    <div
      style={{
        width,
        height: 25,
        border: `3px solid #333`
      }}
    >
      OPS
    </div>
    {show && <div
      style={{
        display: `grid`,
        gap: 4,
        backgroundColor: `#444`,
        padding: 4
      }}
    >
      <input
        type="button"
        value="Save"
        style={inputStyle}
        onClick={save}
      />
      <input
        type="button"
        value="Build"
        style={inputStyle}
        onClick={save}
      />
    </div>}
  </div>
}
