import { codeOptimalize } from "./codeOptymalize"
import { jsCode } from "./jsCode"

const { author, description, gameName, screenWidth, screenHeight } = window.files.config

export const htmlCode = () => {
  return codeOptimalize(`
    <!DOCTYPE html>
    <html lang="en">
    <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <meta name="author" content="${author}">
      <meta name="description" content="${description}">
      <title>${gameName}</title>

      <meta name="keywords" content="${gameName}, ${author}">
      <meta property="og:title" content="${gameName}">
      <meta property="og:description" content="${description}">
      <style>
        html, body {
          background-color:black;
          margin:0;
          border:0;
          padding:0;
          user-select:none;
          width:100%;
          height:100%;
          overflow:hidden;
        }
        canvas {
          display:block;
        }
      </style>
    </head>
    <body>
      <canvas id="gameCanvas" width="${screenWidth}" height="${screenHeight}"></canvas>

      <script>
        ${jsCode()}
      </script>
    </body>
    </html>
  `)
}
