import { useState } from "react"

export const File = ({ old, file, name, main, deep = 0 }) => {
  const [open, setOpen] = useState(main && true)

  return <>
    {!main && <div
      style={{
        cursor: "pointer",
        marginLeft: deep * 10,
        display: "flex",
        flexDirection: "row"
      }}
      onClick={() => {
        setOpen(!open)
      }}
      onContextMenu={({ pageX, pageY }) => {
        window.data.setContextMenu({
          x: pageX,
          y: pageY,
          arr: [[`New file`, () => {
            window.data.editor.setNameInput([``, (newText) => {
              const [newName, extension = `txt`, ...rest] = newText.split(`.`)

              if(!newName || rest.length > 0) {
                return
              }

              file[newName] = {
                type: extension
              }

              window.data.editor.reloadFiles()
            }])
          }, file.type === `folder`], [`New folder`, () => {
            window.data.editor.setNameInput([``, (newText) => {
              for(let i = 0; i < newText.length; i++) {
                if(!`abcdefghijklmnoprstuwxyz-_`.includes(newText[i].toLowerCase())) {
                  return
                }
              }

              file[newText] = {
                type: `folder`
              }

              window.data.editor.reloadFiles()
            }])
          }, file.type === `folder`], [`Rename`, () => {
            window.data.editor.setNameInput([name, (newText) => {
              if(name === newText) {
                return
              }

              if(old[newText]) {
                console.error(`Error`)
              } else {
                delete old[name]
                old[newText] = file
                window.data.editor.reloadFiles()
              }
            }])
          }], [`Delete`, () => {
            delete old[name]
            window.data.editor.reloadFiles()
          }]]
        })
      }}
    >
      {file.type === `folder` && <div
        style={{
          width: 24,
          height: 24,
          textAlign: "center",
          justifySelf: "center",
          borderRadius: 12,
          transform: `rotate(${open ? 90 : 0}deg)`,
          transition: "transform 150ms"
        }}
      >
        {">"}
      </div>}
      <div
        style={{
          marginLeft: file.type !== `folder` && 24
        }}
      >{name}</div>
    </div>}
    {file.type === `folder` && open && <>
      {Object.entries(file).map(([key, value]) => {
        if(key === `type`) {
          return null
        }
        return <File
          old={file}
          file={value}
          name={key}
          key={key}
          deep={deep + 1}
        />
      })}
    </>}
  </>
}
