import { useState } from "react"

export const GameObject = ({ old, object, main, deep = 0 }) => {
  const [open, setOpen] = useState(main && true)

  const { name, childs } = object

  const haveChilds = childs && childs.length > 0

  const onClick = () => {
    window.editor.setInspector(<div
      style={{
        margin: 12
      }}
    >
      <h2>GameObject</h2>
      <div>{`Name: ${name}`}</div>
    </div>)
  }

  const onContextMenu = () => {

  }

  const onMouseDown = () => {
    window.editor.dragData = {
      from: `hierarchy`,
      old,
      file: object,
      name
    }
  }

  const onMouseUp = () => {
    const { dragData } = window.editor

    if(!dragData || dragData.from !== `hierarchy`) {
      return
    }

    if(!childs) {
      object.childs = []
    }

    object.childs.push(dragData.file)
    dragData.old.childs.splice(dragData.old.childs.indexOf(dragData.file), 1)
    window.editor.dragData = {}
    window.editor.reloadFiles()
  }

  return <>
    {!main && <div
      style={{
        cursor: `pointer`,
        marginLeft: deep * 10,
        display: `flex`,
        flexDirection: `row`
      }}
    >
      {haveChilds && <div
        style={{
          width: 24,
          height: 24,
          textAlign: `center`,
          justifySelf: `center`,
          borderRadius: 12,
          transform: `rotate(${open ? 90 : 0}deg)`,
          transition: `transform 150ms`
        }}
        onClick={() => {
          haveChilds && setOpen(!open)
        }}
      >
        {`>`}
      </div>}
      <div
        style={{
          marginLeft: !haveChilds && 24
        }}
        onClick={onClick}
        onContextMenu={onContextMenu}
        onMouseDown={onMouseDown}
        onMouseUp={onMouseUp}
      >{name}</div>
    </div>}
    {haveChilds && open && <>
      {childs.map((newObject) => {
        return <GameObject
          old={object}
          object={newObject}
          key={newObject.name}
          deep={deep + 1}
        />
      })}
    </>}
  </>
}
