import { useEffect, useState } from "react"

export const TextInput = ({ text, parent, access, toNumber }) => {
  const [, setNewText] = useState(parent[access])

  useEffect(() => {
    const handler = () => {
      if(!toNumber) {
        return
      }

      const value = parent[access]

      if(value.length === 0 || !Number(value)) {
        parent[access] = `0`
        return
      }

      parent[access] = Number(value)
    }

    document.addEventListener(`mousedown`, handler)

    return () => {
      document.removeEventListener(`mousedown`, handler)
    }
  }, [])

  return <div
    style={{
      display: `flex`
    }}
  >
    <div
      style={{
        marginRight: 12
      }}
    >
      {text}
    </div>
    <input
      type="text"
      value={parent[access]}
      onChange={({ target }) => {
        let { value } = target

        parent[access] = value
        setNewText(value)
      }}
    />
  </div>
}
