import { useEffect, useState } from "react"

export const NameInput = () => {
  const [{ text, cb }, setNameInput] = useState({})

  useEffect(() => {
    window.data.editor.setNameInput = setNameInput
  })

  return <div>
    {cb && <input
      type="text"
      style={{
        position: "absolute",
        left: "50vw",
        top: "50vh",
        transform: "translate(-50%, -50%)",
        fontSize: 40
      }}
      value={text}
      onChange={({ target }) => {
        setNameInput({
          text: target.value,
          cb
        })
      }}
      onSubmit={() => {
        console.log(`Submit`)
      }}
    />}
  </div>
}
