import { useEffect, useState } from "react"

export const ContextMenu = () => {
  const [{ x, y, arr }, setContextMenu] = useState({})

  const width = 200

  useEffect(() => {
    window.data.contextMenu = (props) => {
      setContextMenu(props)
    }
  })

  const onMouseDown = ({ pageX, pageY }) => {
    if(!(x < pageX && pageX < x + width)) {
      document.removeEventListener(`mousedown`, onMouseDown)
      setContextMenu({})
    }
  }

  const removeContextMenu = () => {
    document.removeEventListener(`mousedown`, onMouseDown)
    setContextMenu({})
  }

  document.addEventListener(`mousedown`, onMouseDown)

  return arr && arr.length ? <div
    style={{
      position: "absolute",
      zIndex: 1,
      left: x,
      top: y,
      width,
      backgroundColor: "#333",
      border: "3px solid #333"
    }}
  >
    {arr.map(([text, fn]) => {
      return <div
      key={text}
        onClick={() => {
          console.log(`test`)
          removeContextMenu()
        }}
        style={{
          cursor: "pointer",
          "&:hover": {
            backgroundColor: "#333"
          }
        }}
      >
        {text}
      </div>
    })}
  </div> : <div></div>
}
