import { useEffect, useState } from "react"
import { File } from "./File"

export const Files = () => {
  const [state, setState] = useState(0)
  useEffect(() => {
    window.data.editor.reloadFiles = () => {
      setState(state + 1)
    }
  })

  return <div
    style={{
      overflow: "scroll"
    }}
    onContextMenu={({ pageX, pageY }) => {
      window.data.setContextMenu({
        x: pageX,
        y: pageY,
        arr: [[`New file`, () => {
          window.data.editor.setNameInput([``, (newText) => {
            const [newName, extension = `txt`, ...rest] = newText.split(`.`)

            if(!newName || rest.length > 0) {
              return
            }

            files[newName] = {
              type: extension
            }

            window.data.editor.reloadFiles()
          }])
        }], [`New folder`, () => {
          window.data.editor.setNameInput([``, (newText) => {
            if(!window.data.editor.alphabet.includes(newText[0].toLowerCase())) {
              return
            }

            for(let i = 1; i < newText.length; i++) {
              if(!`${window.data.editor.alphabet}-_${window.data.editor.numbers}`.includes(newText[i].toLowerCase())) {
                return
              }
            }

            files[newText] = {
              type: `folder`
            }

            window.data.editor.reloadFiles()
          }])
        }]]
      })
    }}
  >
    <File
      file={window.data.files}
      main={true}
    />
  </div>
}
