import { useState } from "react"

export const TextInput = ({ text, parent, access, type = `text` }) => {
  const [newText, setNewText] = useState(parent[access])

  return <div>
    {text}
    <input
      type={type}
      value={newText}
      onChange={({ target }) => {
        parent[access] = target.value
        setNewText(target.value)
      }}
    />
  </div>
}
