const BoolInput = ({ name, text }) => {

}

const NumberInput = ({ name, text }) => {

}

const StringInput = ({ name, text }) => {
  return <div
    style={{
      display: `flex`
    }}
  >
    <div>{name}</div>
    <input
      type={!isBool ? `text` : `checkbox`}
      value={text}
      onChange={({ target }) => {
        console.log(target)
        let { value } = target
        //console.log(value)
        if(isBool) {
          value = value === `true` ? true : false
        }

        //console.log(value)
        window.config[name] = value
        //console.log(window.config)
      }}
    />
  </div>
}

const CustomInput = (props) => {
  const type = typeof props.text

  if(type === `boolean`) {
    return <BoolInput {...props} />
  }

  if(type === `number`) {
    return <NumberInput {...props} />
  }

  return <StringInput {...props} />
}

export const Config = () => {
  return <div>
    {Object.entries(window.config).map(([key, value]) => {
      return <CustomInput
        key={key}
        name={key}
        text={value}
      />
    })}
  </div>
}
