import { useState } from "react"

const boolInput = ({ name, text }) => {

}

const numberInput = ({ name, text }) => {

}

const stringInput = ({ name, text }) => {
  const [currentText, setCurrentText] = useState(text)

  return {
    type: `text`,
    value: currentText,
    onChange: ({ target }) => {
      const { value } = target
      setCurrentText(value)

      window.config[name] = value
    }
  }
}

const CustomInput = (props) => {
  const type = typeof props.text

  let myInput = stringInput(props)

  if(type === `boolean`) {
    myInput = boolInput(props)
  }

  if(type === `number`) {
    myInput = numberInput(props)
  }

  return <div
    style={{
      display: `flex`
    }}
  >
    <div>{props.name}</div>
    <input
      {...myInput}
    />
  </div>
}

export const Config = () => {
  return <div>
    {Object.entries(window.config).map(([key, value]) => {
      return <CustomInput
        key={key}
        name={key}
        text={value}
      />
    })}
  </div>
}
