import { useEffect, useState } from "react"

document.addEventListener(`mousedown`, (e) => {
  if(window.data.isContextMenuShow) {
    window.data.isContextMenuShow = false
    window.data.contextMenu({})
  }
})

export const ContextMenu = ({ first }) => {
  const [{ x, y, arr }, setContextMenu] = useState({})
  window.data.isContextMenuShow = true

  useEffect(() => {
    window.data.contextMenu = (props) => {
      setContextMenu(props)
    }
  })

  return <div>
    {arr && arr.length && <div
      style={{
        position: "absolute",
        zIndex: 1,
        left: x,
        top: y,
        width: 200,
        backgroundColor: "#333",
        border: "3px solid #333"
      }}
    >
      {arr.map(([text, fn]) => {
        return <div
        key={text}
          onClick={fn}
          style={{
            cursor: "pointer",
            "&:hover": {
              backgroundColor: "#333"
            }
          }}
        >
          {text}
        </div>
      })}
    </div>}
  </div>
}
