import { useState } from "react"
import { useHover } from "../../lib/useHover"

export const GameObject = ({ old, name, object, main, deep = 0 }) => {
  const [open, setOpen] = useState(main && true)
  const hover = useHover({
    color: `#555`
  })

  const { ...childs } = object

  const haveChilds = Object.keys(childs)?.length > 0

  const onClick = () => {
    window.editor.setInspector(<div
      style={{
        margin: 12
      }}
    >
      <h2>GameObject</h2>
      <div>{`Name: ${name}`}</div>
    </div>)
  }

  const onContextMenu = () => {

  }

  const onMouseDown = () => {
    window.editor.dragData = {
      old,
      file: object,
      name
    }
  }

  const onMouseUp = () => {
    const { dragData } = window.editor

    if(!dragData || dragData.name === name) {
      return
    }

    for(const key in childs) {
      if(key === dragData.name) {
        return
      }
    }

    object[dragData.name] = dragData.file
    if(dragData.from === `hierarchy`) {
      delete dragData.old[dragData.name]
    }

    window.editor.dragData = {}
    window.editor.reloadHierarchy()
  }

  return <>
    {!main && <div
      style={{
        marginLeft: deep * 10,
        display: `flex`,
        flexDirection: `row`,
        "&:hover": {
          color: `blue`
        },
      }}
    >
      {haveChilds && <div
        style={{
          cursor: `pointer`,
          width: 24,
          height: 24,
          textAlign: `center`,
          justifySelf: `center`,
          borderRadius: 12,
          transform: `rotate(${open ? 90 : 0}deg)`,
          transition: `transform 150ms`
        }}
        onClick={() => {
          haveChilds && setOpen(!open)
        }}
      >
        {`>`}
      </div>}
      <div
        {...hover}
        style={{
          cursor: `pointer`,
          hover: {
            color: `blue`
          },
          marginLeft: !haveChilds && 24,
          ...hover.style
        }}
        onClick={onClick}
        onContextMenu={onContextMenu}
        onMouseDown={onMouseDown}
        onMouseUp={onMouseUp}
      >{name}</div>
    </div>}
    {haveChilds && open && <>
      {Object.keys(childs).map((key, value) => {
        return <GameObject
          old={object}
          object={value}
          key={key}
          name={key}
          deep={deep + 1}
        />
      })}
    </>}
  </>
}
