import { useEffect, useRef, useState } from "react"

export const NameInput = () => {
  const [{ text, cb }, setNameInput] = useState({})
  const ref = useRef()

  const ret = () => {
    cb(text)
    setNameInput({})
  }

  useEffect(() => {
    window.data.editor.setNameInput = setNameInput

    const handler = ({ target }) => {
      if(ref.current) {
        if(!ref.current.contains(target)) {
          ret()
        }
      }
    }

    document.addEventListener(`mousedown`, handler)

    return () => {
      document.removeEventListener(`mousedown`, handler)
    }
  })

  return <div>
    {cb && <input
      type="text"
      ref={ref}
      style={{
        position: "absolute",
        left: "50vw",
        top: "50vh",
        transform: "translate(-50%, -50%)",
        fontSize: 40
      }}
      value={text}
      onChange={({ target }) => {
        setNameInput({
          text: target.value,
          cb
        })
      }}
      on={ret}
    />}
  </div>
}
