import { useState } from "react"

export const TextInput = ({ text, parent, access, toNumber }) => {
  const [, setNewText] = useState(parent[access])

  return <div
    style={{
      display: `flex`
    }}
  >
    <div
      style={{
        marginRight: 12
      }}
    >
      {text}
    </div>
    <input
      type="text"
      value={parent[access]}
      onChange={({ target }) => {
        let { value } = target

        const last = value[value.length - 1]

        if(!`0123456789.`.includes(last)) {
          return
        }

        if(last === `.`) {
          const oldValue = value.slice(0, -1)
          if(oldValue.includes(`.`)) {
            return
          }
        } else {
          if(value.length === 0) {
            value = 0
          } else {
            if(toNumber) {
              value = Number(value)
            }
          }
        }

        parent[access] = value
        setNewText(value)
      }}
      onSubmit={() => {
        console.log(`sfdhfkjsh`)
      }}
    />
  </div>
}
