import { useState } from "react"

export const TextInput = ({ text, parent, access }) => {
  const [, setNewText] = useState(parent[access])

  return <div
    style={{
      display: `flex`,
      //dis
    }}
  >
    <div>
      {text}
    </div>
    <input
      type="text"
      value={parent[access]}
      onChange={({ target }) => {
        parent[access] = target.value
        setNewText(target.value)
      }}
    />
  </div>
}
