    return () => {
      document.removeEventListener(`mousemove`, handler)
    }
  })

  return <div
    onMouseMove={onMouseMove}
    style={{
      position: `absolute`,
      ...cursorPosition
    }}
  >
    TEXT
    {dragData && dragData.name}
  </div>
}
