const boolInput = ({ name, text }) => {

}

const numberInput = ({ name, text }) => {

}

const stringInput = ({ name, text }) => {
  return {
    type: !isBool ? `text` : `checkbox`,
    value: text,
    onChange: ({ target }) => {
      let { value } = target
      //console.log(value)
      if(isBool) {
        value = value === `true` ? true : false
      }

      //console.log(value)
      window.config[name] = value
      //console.log(window.config)
    }
  }
}

const CustomInput = (props) => {
  const type = typeof props.text

  myInput = stringInput(props)

  if(type === `boolean`) {
    myInput = boolInput(props)
  }

  if(type === `number`) {
    myInput = numberInput(props)
  }

  return <div
    style={{
      display: `flex`
    }}
  >
    <div>{name}</div>
    <input
      {...myInput}
    />
  </div>
}

export const Config = () => {
  return <div>
    {Object.entries(window.config).map(([key, value]) => {
      return <CustomInput
        key={key}
        name={key}
        text={value}
      />
    })}
  </div>
}
