import { useEffect, useState } from "react"

export const NameInput = () => {
  const [{ text, cb }, setNameInput] = useState({})

  useEffect(() => {
    window.data.editor.setNameInput = setNameInput
  })

  const width = 500
  const height = 100

  return <div>
    {cb && <input
      type="text"
      style={{
        position: "absolute",
        width,
        height,
        //left: (window.innerWidth - width) / 2,
        //top: (window.innerHeight - height) / 2,
      alignItems: 'center',
      justifyContent: 'center',
        fontSize: 40
      }}
      value={text}
      onChange={({ target }) => {
        setNameInput({
          text: target.value,
          cb
        })
      }}
      onSubmit={() => {
        console.log(`Submit`)
      }}
    />}
  </div>
}
