import { useEffect, useState } from "react"

export const createContextMenu = (e, arr) => {
  e.preventDefault() // prevent the default behaviour when right clicked
  console.log("Right Click")

  window.data.contextMenu({
    x: e.pageX,
    y: e.pageY,
    arr
  })
}


export const ContextMenu = ({ first }) => {
  const [{ x, y, arr }, setContextMenu] = useState({})

  useEffect(() => {
    window.data.contextMenu = (props) => {
      setContextMenu(props)
    }
  })

  return !first && arr && arr.length && <div
    style={{
      display: "absolute",
      top: x,
      left: y,
      width: 200,
      height: 300,
      backgroundColor: "#333"
    }}
  >
    {`${x}, ${y}, ${arr.length}`}
  </div>
}
