import { useState } from "react"
import { save } from "./save"

export const EditorOpctions = () => {
  const [show, setShow] = useState(false)

  const inputStyle = {
    border: 0,
    padding: `8px 16px`,
    width: 100,
    height: 30
  }

  return <div
    style={{
      position: `absolute`,
      zIndex: 1,
      display: `flex`,
      flexDirection: `column`,
      left: window.editor.width - 100
    }}
    onMouseEnter={() => {
      setShow(true)
    }}
    onMouseLeave={() => {
      setShow(false)
    }}
  >
    <div
      style={{
        width: 100,
        height: 100
      }}
    >
      OPS
    </div>
    {show && <input
      type="button"
      value="Save"
      style={inputStyle}
      onClick={save}
    />}
  </div>
}
