import { ContextMenu } from "./ContextMenu"
import { save } from "./save"

export const UI = () => {
  const inputStyle = {
    border: 0,
    margin: "8px 16px"
  }

  return <div
    style={{
      position: "absolute",
      zIndex: 0,
      right: 0,
      top: 0,
      width: window.data.editor.width,
      height: window.data.editor.height
    }}
  >
    <div
      style={{
        display: "absolute",
        display: "flex",
        flexDirection: "row"
        }}
      >
      <input
        type="button"
        value="Save"
        style={inputStyle}
        onClick={save}
      />
    </div>
    <ContextMenu
      first={true}
    />
  </div>
}
