const CustomInput = ({ name, text }) => {
  let isBool = typeof text === `boolean`

  return <div
    style={{
      display: `flex`
    }}
  >
    <div>{name}</div>
    <input
      type={!isBool ? `text` : `checkbox`}
      value={text}
      onChange={({ target }) => {
        let { value } = target
        if(isBool) {
          value = value === `true` ? true : false
        }

        console.log(value)
        window.config[name] = value
        console.log(window.config)
      }}
    />
  </div>
}

export const Config = () => {
  return <div>
    {Object.entries(window.config).map(([key, value]) => {
      return <CustomInput
        key={key}
        name={key}
        text={value}
      />
    })}
  </div>
}
